import React from 'react';
import './Auth.css'
import logo from '../assets/stack-overflow.ico'
import {useState} from 'react'


function Auth(){
    const [isSignup, setIsSignup] = useState(false)
    
    const handleSwitch = () => {
        setIsSignup(!isSignup);
    }
    return(
        <section class='auth-section'>
            <div class="auth-container">
                { !isSignup && <img src = {logo} class="login-logo" alt='stack_overflow'/>}
                <form>
                <br/>
                <br/>
                {isSignup && (<label htmlFor ='name'>
                <h4>Display Name</h4>
                <input type="text" id ='name' name='name'/>
                </label>)}
                    <label htmlFor="email">
                        <h4>Email</h4>
                        <input type="email" name= 'email' id ='email'/>
                    </label>
                    <label htmlFor="password">
                       <div> <h4>Password</h4>
                        {!isSignup && <h4> Forgot Password</h4>}
                        </div> <input type="password" name= 'password' id ='password'/>
                        { isSignup&& <p> Password must contain at least eight characters, including atleast 1 number and 1 letter</p>}
                    </label>
                    {
                        isSignup && (
                            <label htmlFor='check'>
                                <input type="checkbox" id ='check'/>
                                <p>opt in to recieve occassional ,<br/>
                                product updates, user research invitations,<br/>
                                company announcements, and digests.
                                </p>
                            </label>
                        )
                    }
                    <button type='submit' className='auth-btn' >{isSignup ? 'Signup' : 'Login'}</button>
                    {
                        isSignup && (
                            <p>
                                By clicking "Sign up" you agree to out <apn></apn>
                            </p>
                        )
                    }
                </form>
                <p>
                    {isSignup ? 'already have an account?' : " Don't  have an account?"}
                    <button type='button' className='handle-switch-btn' onClick={handleSwitch}>{isSignup ? "Login" : "Signup"}</button>
                </p>
            </div>
        </section>
 
    );
}

export default Auth;
