import React from 'react'
import './Questions.css';
import { useParams } from 'react-router-dom';
import upvote from '../../assets/sort-up-solid.svg';
import downvote from '../../assets/sort-down-solid.svg'

function  QuestionsDetails(){
  var  id  = {};
  id = useParams();
  console.log(id._id );

  var questionsList = [{
    _id :1,
    upVotes : 3,
    downVotes :2,
    noOfAnswers:2,
    questionTitle: "what is a function",
    questionBody : "It meant to be",
    questionTags : ['java','node js', 'react js' , 'mango db'],
    userPosted : "mano",
    userId: 1,
    askedOn: "jan 1",
    answer: [{
        answerBody: "Answer",
        userAnswered: 'kumar',
        answeredOn: "jan 2",
        userId: 2,
    }]
},

{
    _id :'2',
    upVotes : 3,
    downVotes :2,
     noOfAnswers:0,
    questionTitle: "what is a function",
    questionBody : "It meant to be",
    questionTags : ['java','node js', 'react js' , 'mango db'],
    userPosted : "manoj",
    askedOn: "jan 1",
    userId: 1,
    answer: [{
        answerBody: "Answer",
        userAnswered: 'kumar',
        answeredOn: "jan 2",
        userId: 2,
    }]
  },
{
    _id :'3',
    upVotes : 3,
    downVotes :2,
    noOfAnswers:2,
    questionTitle: "what is a function",
    questionBody : "It meant to be",
    questionTags : ['java','node js', 'react js' , 'mango db'],
    userPosted : "mano",
    askedOn: "jan 1",
    userId: 1,
    answer: [{
        answerBody: "Answer",
        userAnswered: 'kumar',
        answeredOn: "jan 2",
        userId: 2,
    }]
  }
]
    // return <h1>ddd</h1>
    return  <div className='question-details-page'>
{
  questionsList === null ?
  <h1>Loading...</h1> :
  <>

    {
      questionsList.filter(question => question._id == id._id).map(question =>(
        <div key={question._id}>
        {console.log(question)}
        <section className='question-details-container'>
        <h1>{question.questionTitle}</h1>

        <div className='question-details-container-2'>
        <div className='question-votes'><img src={upvote} width='18' alt = ""/>
        <p>{question.upVotes- question.downVotes}</p>
        <img src={downvote} width='18' alt="" />
        </div>
        <div style={{width: '100%'}}>
        <p className='question-body'>{question.questionBody}</p>
        <div className='question-details-tags'>
          {
            question.questionTags.map((tag) => {
              <p key={tag}>{tag}</p>
              console.log(tag);
              console.log("satisfies");

              <h1>fdfd</h1>

            })
          }
        </div>
        </div>
        </div>

        </section>

        </div>
       ) )
    }
  </>
}
</div>      
}
export default QuestionsDetails;
