import './Questions.css';
import { useParams } from 'react-router-dom';
// import upvote from '../../../src/assets/upvote.svg'
// import downvote from '../../../src/assets/downvote.svg'

function  QuestionsDetails(){
  const { id } = useParams();

  var questionsList = [{
    _id :1,
    upVotes : 3,
    downVotes :2,
    noOfAnswers:2,
    questionTitle: "what is a function",
    questionBody : "It meant to be",
    questionTags : ['java','node js', 'react js' , 'mango db'],
    userPosted : "mano",
    userId: 1,
    askedOn: "jan 1",
    answer: [{
        answerBody: "Answer",
        userAnswered: 'kumar',
        answeredOn: "jan 2",
        userId: 2,
    }]
},

{
    _id :2,
    upVotes : 3,
    downVotes :2,
     noOfAnswers:0,
    questionTitle: "what is a function",
    questionBody : "It meant to be",
    questionTags : ['java','node js', 'react js' , 'mango db'],
    userPosted : "manoj",
    askedOn: "jan 1",
    userId: 1,
    answer: [{
        answerBody: "Answer",
        userAnswered: 'kumar',
        answeredOn: "jan 2",
        userId: 2,
    }]
  },
{
    _id :3,
    upVotes : 3,
    downVotes :2,
    noOfAnswers:2,
    questionTitle: "what is a function",
    questionBody : "It meant to be",
    questionTags : ['java','node js', 'react js' , 'mango db'],
    userPosted : "mano",
    askedOn: "jan 1",
    userId: 1,
    answer: [{
        answerBody: "Answer",
        userAnswered: 'kumar',
        answeredOn: "jan 2",
        userId: 2,
    }]
  }
]
    return
        (
<div className='question-details-page'>
{
  questionsList ===null ?
  <h1>Loading...</h1> :
  <>
    {
      questionsList.filter(question => question._id === id).map(question =>(
        <div key={question._id}>
        console.log(question);
        <section className='question-details-container'>
        <h1>{question.questionTitle}</h1>
        <div className='question-details-container-2'>
        <div className='question-votes'><img src={upvote} alt = ""/>
        <p>{question.upVotes- question.downVotes}</p>
        <img src={downvote} alt="" />
        </div>
        </div>

        </section>

        </div>
       ) )
    }
  </>
}
</div>      );
}

export default QuestionsDetails;
