import React from 'react';
import './HomeMainbar.css';
import { Link, useLocation } from 'react-router-dom';


const HomeMainbar =() =>{

    const location= useLocation();
    return(
        <div className='Home' >
        <div className='HomeHeading'>
        <Router>
        { 
            location.pathname ==='/' ? <h1 className='AllHeading'>Top Questions</h1> : <h1>All Questions</h1>}; </Router>
        <button class='AskButton' type="submit"> Ask Question</button>
        </div>
        </div>)
}

export default HomeMainbar;
