import React from 'react';
import './HomeMainbar.css';
import {useLocation, Link} from 'react-router-dom';
import Questions from './Questions';
import QuestionList from './QuestionList';
const HomeMainbar =() => {

    var questionsList = [{
        id :1,
        votes : 3,
        noOfAnswers:2,
        questionTitle: "what is a function",
        questionBody : "It meant to be",
        questionTags : ['java','node js', 'react js' , 'mango db'],
        userPosted : "mano",
        askedOn: "jan 1"
    },

    {
        id :2,
        votes : 0,
        noOfAnswers:0,
        questionTitle: "what is a function",
        questionBody : "It meant to be",
        questionTags : ['java','node js', 'react js' , 'mango db'],
        userPosted : "manojjjjjjjjjjjjjjjjjjjjjjjjjjjjjjjjjjjjjjjjjjjjjjjjjjjjjjjj",
        askedOn: "jan 1"
    },

    {
        id :3,
        votes : 3,
        noOfAnswers:2,
        questionTitle: "what is a function",
        questionBody : "It meant to be",
        questionTags : ['java','node js', 'react js' , 'mango db'],
        userPosted : "mano",
        askedOn: "jan 1"
    }
]

    const location = useLocation()
    return(
        <div className='main-bar' >
        <div className='main-bar-header'>
        {
            location.pathname === '/' ? <h1>Top Questions</h1>: <h1>Ask Questions</h1>
        }
            <Link to= '/AskQuestion' className='ask-btn'><button class='AskButton' type="submit"> Ask Question</button></Link>
        
        </div>

        
        {
            questionsList === null ?
            <h1>Loading...</h1> :
            <>
                <p>{questionsList.length } questions</p>
                <QuestionList questionsList= {questionsList} />
                
            </>

        }
</div>
        )
}

export default HomeMainbar;
